SELECT su.id, su.username, su.password, su.name, su.type, su.phone, su.avatar_url, su.additional_info, su.post_id, su.status, sp.name post_name FROM system_user su LEFT JOIN system_post sp ON sp.is_deleted=0 AND sp.id=post_id
su.is_deleted=0
AND name = #{queryVo.name}
AND phone = #{queryVo.phone}
